import { createStore, applyMiddleware, Store } from 'redux';
import thunkMiddleWare from 'redux-thunk';
// import {compositeWithDevTools } from 'redux-devtools-extension';
import {createLogger} from 'redux-logger';
import rootReducer from './reducers';

const middleware = [
    createLogger(),
    thunkMiddleWare
];
let store = null;

export function configureStore({initialState} : {initialState:any}){
    store = createStore(rootReducer,initialState,applyMiddleware(...middleware));
    return store
}

export function getStoreInstance():Store {
    return store;
}
