import React,{ Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../layout/Spinner';

import { fetchMovies,setLoading } from '../../actions/searchActions';

export class Movie extends Component {
    componentDidMount() {
        this.props.fetchMovie(this.props.match.params.id);
        this.props.setLoading();
    }
    render() {
        const {loading,movie } = this.props;
        let movieinfo = (
            <div className="container">
                <div className="row">
                    <div className="col-md-4 card card-body">
                        <img src={`https://image.tmdb.org/t/p/w1280${movie.poster_path}`} className="thumbnail" alt="Poster" />
          </div>
          <div className="col-md-8">
            <h2 className="mb-4">{movie.original_title}</h2>
            <ul className="list-group">
              <li className="list-group-item">
                <strong>Released:</strong> {movie.release_date}
              </li>
              <li className="list-group-item">
                <strong>Vote Count</strong> {movie.vote_count}
              </li>
              <li className="list-group-item">
                <strong>Rated:</strong> {movie.vote_average}
              
              </li>
            </ul>
          </div>
        </div>
        <Link to="/" className="btn btn-default text-light">
                Go Back To Search
              </Link>
        </div>
        );
        let content = loading ? <Spinner /> : movieInfo;
    return <div>{content}</div>;

    }
}

const mapStateToProps = state => ({
    loading: state.movies.loading,
    movie: state.movies.movie
});

export default connect(
    mapStateToProps,{fetchMovie,setLoading}
)(Movie);
